const initState = 0;//初始化状态
export default function countReducer(perState = initState, action) {
    //从action对象中获取：type、data
    const { type, data } = action;
    //根据type决定如何加工数据
    switch (type) {
        case 'increment':
            return perState + data;
        default:
            return perState;
    }
}
