import React, { Component } from 'react'
import Child from './Child'
import './index.css'
export default class Parent extends Component {

    static getDerivedStateFromError(error) {
        console.log(error);
        return { hasError: error }
    }

    render() {
        return (
            <div className="parent">
                <h2>我是Parent组件</h2>
                <Child />
            </div>
        )
    }
}
