import React, { Component } from 'react'
import './index.css'
export default class Parent extends Component {

    state = { name: 'Tom' }

    render() {
        const { name } = this.state;
        
        return (
            <div className="parent">
                <h2>我是Parent组件</h2>
                <A render={(name) => { return <B /> }} />
            </div>
        )
    }
}

class A extends Component {
    render() {
        return (
            <div className="a">
                <h2>我是A组件</h2>
                {
                    this.props.render()
                }
            </div>
        )
    }
}


class B extends Component {
    render() {
        return (
            <div className="b">
                <h2>我是B组件</h2>
            </div>
        )
    }
}
